class Dashboard extends Component{
    state = {
        fullname: ''
    }
    componentWillMount(){
        const a = window.location.href.split('/');
        const id = a[a.length - 1]
        fetch(`http://localhost:3000/api/users/${id}`)
        .then(result=>result.json())
        .then(result=>{
          this.setState({
              fullname: result.fullname
          })
        })
        .catch(err=>console.log(err))
        }

    render(){
